import React, { useRef, useState } from "react";
import "./TodoInput.css";

const TodoInput = ({ onAdd }) => {
  const textRef = useRef();
  const [text, setText, setTit] = useState("");

  const changeInput = (evt) => {
    const { value } = evt.target;
    setText(value);
  };

  const changeTit = (evt) => {
    const { value } = evt.target;
    setTit(value);
  };

  const onSubmit = (evt) => {
    evt.preventDefault(); //새로고침 방지

    if (!text) return; //text에 아무것도 없을 때 - 공백 입력 방지
    if (!textTit) return; //text에 아무것도 없을 때 - 공백 입력 방지

    onAdd(text);
    onAdd(textTit);

    setText("");
    textRef.current.focus();
  };

  return (
    <form className="TodoInput" onSubmit={onSubmit}>
      <input
        type="text"
        value={textTit}
        onChange={changeTit}
        ref={textRef}
        placeholder="입력하세요"
        maxLength={35}
      />
      <input
        type="text"
        value={text}
        onChange={changeInput}
        ref={textRef}
        placeholder="입력하세요"
        maxLength={35}
      />
      <button>버튼</button>
    </form>
  );
};

export default TodoInput;
