import React, { useRef, useState } from "react";
import TodoInput from "./TodoInput";
import TodoList from "./TodoList";
import "./Todo.css";

const Todo = () => {
  const no = useRef(1);

  const [todos, setTodo] = useState([]);

  const onAdd = (text) => {
    setTodo([
      ...todos,
      {
        id: no.current++,
        text: text,
        done: false,
      },
    ]);
  };

  const onDel = (id) => {
    setTodo(todos.filter((todo) => todo.id !== id));
  };

  const onToggle = (id) => {
    setTodo(
      todos.map((todo) =>
        todo.id === id
          ? {
              ...todo,
              done: !todo.done,
            }
          : todo
      )
    );
  };

  return (
    <div className="Todos">
      <h1>Todo List</h1>
      <TodoInput onAdd={onAdd} />
      <TodoList todos={todos} onDel={onDel} onToggle={onToggle} />
    </div>
  );
};

export default Todo;
